float random(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec3 color = vec3(0.);
    vec2 p2 = p;
    // rgb(208,51,72)
    // rgb(120,107,105)
    // rgb(160,129,107)
    // rgb(152,93,119)
    //rgb(186,153,121)
    //rgb(201,217,234)

    vec3 sky = normalize(vec3(201,217,234));
    vec3 rcol = normalize(vec3(208,51,72));
    vec3 bcol = normalize(vec3(120,107,105));
    bcol = normalize(vec3(57,55,56));
    vec3 fcol = normalize(vec3(152,93,119));

    p *= 3.;
    float ring = noise(p);
    float body = noise(p * vec2(70.,10.) );
    float Floor = noise(p* 100.);


    fcol = fcol * (Floor + 1.)/2.;
    bcol = bcol * body;

    if(p2.y > .3 && p2.y < .5)
    {
        color = rcol;
    }else{
        vec3 leg = mix(vec3(0.),normalize(vec3(186,153,121)),body);
        vec3 body = vec3(0.);
        color = mix(body , leg,-p.y-1.);
    }
    color =mix( sky,color,step(length(p2) ,1.));

    if(p2.y + sin(p.x * 10.+iTime)/50. <-.8 && sky == color)
    {
        color = fcol;
    }
    fragColor = vec4(color,1.0);
}
