// Vietnam flag
// I learned this from this shader: https://www.shadertoy.com/view/flsXRM

vec3 RED = vec3(218, 37, 29)/255.;
vec3 YELLOW = vec3(1, 1, 0);
vec3 WHITE = vec3(1);
vec3 BLUE = vec3(60, 59, 110)/255.;

float Star(vec2 uv, float size) {
    uv.x = abs(uv.x);

    float a = 6.2832/5.;
    float d1 = dot(uv, vec2(sin(a), cos(a)));
    a = 3.*6.2832/5.;
    float d2 = dot(uv, vec2(sin(a), cos(a)));

    a = 2.*6.2832/5.;
    float d4 = dot(uv, vec2(sin(a), cos(a)));

    float d = min(max(d1, d2), max(uv.y, d4));
    float w = min(fwidth(d), 0.05);

    return smoothstep(w, -w, d-size);
}

vec2 Remap(vec2 p, float b, float l, float t, float r) {
    return vec2( (p.x-l) / (r-l), (p.y-b) / (t-b));
}

vec3 Flag(vec2 uv) {
    vec3 col = RED;

    float star = Star(uv, 0.07);
    col = mix(RED, YELLOW, star);

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = uv.x*7.-2.*iTime+uv.y*3.;
    uv.y += sin(t)*.05;
    vec2 st = (fragCoord-0.5*iResolution.xy) / iResolution.y;

    vec3 col = Flag(st);

    col *= .7+cos(t)*.3;

    fragColor = vec4(col,1.0);
}
