void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-vec2(iResolution.x, 0)*.5)/iResolution.y;
    uv.x -= iTime*.4;
    uv.x *= .166;
    uv *= 4.;

    vec2 d = vec2(log(mod(iTime*4.+floor(uv.y)*.8+floor(abs(uv.x)*6.+0.03)*.1,3.)+2.7)*.04-.069,0);

    // Time varying pixel color
    vec3 col = texture(iChannel0,uv+d).xxx;
    if (!compV3(col,.9))
    {
        col = vec3(1, .8, .3);
    }
    else{
    col = texture(iChannel0,uv).xxx;
    if(!compV3(col,.9))
    {
        col = vec3(1,.6,.3);
    }
    else{
    col = texture(iChannel0,uv-d).xxx;
    if(!compV3(col,.9))
    {
        col = vec3(.6,.3,.9);
    }
    }}

    // Output to screen
    fragColor = vec4(col,1.0);
}
