// lembrar de fazer um circuito

void mainImage(out vec4 O, vec2 u){
    vec3  q, r = iResolution, C;       
    float i, a, s, t = iTime * 4.;

    O *= a; 
    while(i++ < 2e2){
        s = a + t;
        q = mod(
              vec3((u + u - r.xy) / r.y * a / 4.
                     * mat2(cos(vec4(0, 11, 33, 0) + t / 18.))
                     + vec2(  
                         cos(t * .3) - cos(s * .3) + 3.14,
                         sin(t * .4) - sin(s * .4) + 3.14
                   ) * .8, s
              ),
              2.
        ) - 1.;

        int j ;
        while(j++ < 3) 
            q = abs(2. * q),
            q = q.x < q.y ? q.zxy : q.zyx,
            q -= vec3(3, 3, 4);

        C = .1 * cos(s * 3.14 / 8. + vec3(2, 4, 6));

        s = abs(length(q.xz) / 23. - .03 + cos(mod(s/2., 1.1) * 6.14) * .02) + 2e-5;

        a += s * 1.5;

        O.rgb += C * C / exp(i * i / 24. * s);
    }
}
