vec3 line(vec2 uv,vec2 pos,float width) {
    vec2 c1=smoothstep(pos-vec2(width/2.,0.),pos-vec2(width/2.,0.)+.00001,uv);
    vec2 c2=smoothstep(pos+vec2(width/2.,0.),pos+vec2(width/2.,0.)+.00001,uv);
    return vec3(c1.x*c1.y*(1.-c2.x*c2.y));
}

float random(in vec2 _st) {
    return fract(sin(dot(_st.xy,vec2(1422.9898,7248.233)))*23.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ys=100.;
    vec2 uv=fragCoord/iResolution.xy;
    float r1=random(vec2(2143.51,floor(uv.y*ys)));
    uv*=vec2(.001,1.);
    uv-=vec2(r1*.001*iTime,0.);
    float speed=r1*.5;
    float r2=random(vec2(uv.x*10.-354.53,floor(uv.y*ys)));
    vec3 col=line(uv,vec2(r2,.0),r1*((1.-iMouse.x/iResolution.x)*3.+.01));
    // erase some lines
    col*=mod(uv.y*ys,2.)>1.?0.:1.;
    fragColor = vec4(1.-col,1.);
}
