// from https://twitter.com/zozuar/status/1621229990267310081

mat2 Rotate(float a) {
  float s = sin(a);
  float c = cos(a);
  return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime; 
    vec4 o;
    vec2 n,q, p = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;

    float d=dot(p,p),S=11.,i,a,j;

    for(mat2 m=Rotate(5.); j++<30.;){
      p*=m;
      n*=m;
      q=p*S+t*1.+sin(t*4.-d*6.)*.4+j+n;
      a+=dot(cos(q)/S,vec2(.23));
      n-=sin(q);
      S*=1.18;
    }
    o+=(a+.4)*vec4(4,2,1,0)+a+a-d;
    o *= 1.-d;
    o.b += (1.-a*100.)*(d*.2);

    fragColor = o;
}
