float u_speed = 0.5; // Set the speed between 0 and 1.0

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 bgColor = mix(vec3(0.1, 0.15, 0.3), vec3(0.0, 0.05, 0.1), uv.y);

    float streakAmount = 0.0;
    for (int i = 0; i < 12; ++i) {
        float lineY = float(i + 1) / 13.0;
        float posY = mod(iTime * u_speed * 2.5 + lineY, 1.0);
        float streakWidth = 0.01;

        streakAmount += smoothstep(streakWidth, 0.0, abs(uv.y - posY)) * 0.5;
    }

    // VHS tracking effect
    float trackingY = mod(iTime * u_speed * 0.5, 1.0);
    float trackingWidth = 0.05;
    float trackingIntensity = smoothstep(trackingWidth, 0.0, abs(uv.y - trackingY));
    streakAmount += trackingIntensity * rand(uv * 10.0 + vec2(iTime * 2.0, 0.0)) * 2.0;

    vec3 streakColor = vec3(0.2, 0.4, 0.6) * streakAmount;
    fragColor = vec4(bgColor + streakColor, 1.0);
}
