void mainImage( out vec4 rgba, in vec2 xy )
{   
    vec3 S=vec3(0.);
    for(int j=0;j<25;++j) { 
        vec3 s=vec3(1.); float t=(iTime+float(j)/(10.*25.))*1.;
        vec2 uv = mat2(cos(t),sin(t),-sin(t),cos(t))*(((xy+vec2(j%5,j/5)/5.)-iResolution.xy/2.)/iResolution.y);
        float a=length(uv)*3.1415*mix(0.1,0.8,0.5+0.5*cos(1.618*t)), b=atan(-uv.y, -uv.x)+3.1415;
        uv = vec2(cos(b)*sin(a)/(1.+cos(a))+2.*t,sin(b)*sin(a)/(1.+cos(a))+0.7*t);
        for(int i=0;i<3;++i) {
            float f=6.283*float(i)/3.; vec2 d = vec2(cos(f),sin(f));
            s*=sin(40.*length(d*dot(uv, d)-uv)*sign(dot(uv, vec2(-d.y,d.x)))+t)*sin(6.282*(vec3(j)/25.+vec3(0.,t*0.33,0.66)));
        } S+=pow(max(vec3(0.), s),vec3(1./3.));
    } rgba = vec4(pow(3.*S/25.,vec3(1.5)), 1.);
}
