void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Convert pixel coordinates to a normalized range (-1 to 1)
    vec2 p = (2.0*fragCoord-iResolution.xy)/min(iResolution.y,iResolution.x);

    // Calculate the value of xy using your equation
    float xy = p.x / p.y;

    // Define a threshold for plotting the point
    float threshold = 2.; // Adjust this value as needed // air: 1.0, water: 1.33, glass: 1.5, diamond: 2.4

    // Check if xy is within the threshold to plot the point
    if (abs(xy) < abs(threshold))
    {
float redMultiplier = 0.1;
float greenMultiplier = 1.1;
float blueMultiplier = 1.1;
float angle = 1.11;
    vec2 normalizedCoord  = fragCoord/iResolution.xy*2.0-1.0;
   normalizedCoord= fract(normalizedCoord * 0.5) - 0.5;

for(float i = 0.0; i<18.0; i+=1.0){
 normalizedCoord =abs(normalizedCoord );
 normalizedCoord -=0.6;
  normalizedCoord *=1.3;
  normalizedCoord *= mat2(
  cos(angle),-sin(angle),
  sin(angle),cos(angle)
  );
}

    fragColor = vec4(length(normalizedCoord)*redMultiplier,length(normalizedCoord)*greenMultiplier,length(normalizedCoord)*blueMultiplier,1.0);
    }
    else
    {
float angle = 5.0+iTime*0.01;
vec2 normalizedCoord = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
for(float i = 0.0; i< 16.0; i+=1.0){
 normalizedCoord =abs(normalizedCoord );
 normalizedCoord -=0.6;
  normalizedCoord *=1.3;
  normalizedCoord *= mat2(
  cos(angle),-sin(angle),
  sin(angle),cos(angle)
  );
}


    fragColor = vec4(cos(length(normalizedCoord+vec2(.5,-0.7))),cos(length(normalizedCoord+vec2(0.2,-0.3))),length(normalizedCoord+vec2(-0.4,-0.1)),1.0);
    }
}
