void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize the fragment coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // Define the base color
    vec3 baseColor = vec3(0.0);

    // Create a swirling wave pattern
    float timeFactor = iTime * 0.5;
    float waveX = sin(uv.x * 10.0 + timeFactor);
    float waveY = cos(uv.y * 10.0 + timeFactor);
    float wave = waveX + waveY;

    // Apply color gradients based on the wave amplitude
    vec3 color = mix(vec3(0.0, 0.2, 0.8), vec3(0.8, 0.0, 0.2), wave * 0.5 + 0.5);

    // Create a shiny effect
    float shininess = 1.0 - abs(sin(wave * 10.0 + timeFactor));
    color *= shininess;

    // Output the final color
    fragColor = vec4(color, 1.0);
}
