//gaussian blur
#define K 5
#define SIGMA .85

float gaussian(vec2 pos){
    float left=1./(2.*PI*SIGMA*SIGMA);
    float right=exp(-dot(pos, pos)/(2.*SIGMA*SIGMA));
    return left*right;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv=fragCoord/iResolution.xy;

    vec4 cSum=vec4(0);
    float wSum=0.;
    for(int i=-K;i<=K;i++){
        for(int j=-K;j<=K;j++){
            vec2 offset=vec2(i, j);
            float weight=gaussian(offset);
            wSum+=weight;

            vec2 offsetUV=offset/iResolution.xy;
            vec4 col=texture(iChannel0, uv+offsetUV);
            cSum+=col*weight;
        }
    }
    vec4 avg=cSum/wSum;
    fragColor=avg;
    fragColor*=1.-avg.a;
}

#define N 3//[1, 5]
#define LN_SZ 6.
#define VN_SCL 14.
#define ANIM_SPEED 5.//fps

#define LUM(a) dot(a, vec3(.299, .587, .114))

mat3 sobelX=mat3(
    -1, 0, 1,
    -2, 0, 2,
    -1, 0, 1
);

mat3 sobelY=mat3(
    1, 2, 1,
    0, 0, 0,
    -1, -2, -1
);

//voronoi from inigo quilez
//https://www.shadertoy.com/view/MslGD8
vec2 voronoi(vec2 x){
    vec2 n=floor(x);
    vec2 f=x-n;
	vec3 m=vec3(8);
    float time=round(iTime*ANIM_SPEED)/ANIM_SPEED;
    for(int j=-1;j<=1;j++){
        for(int i=-1;i<=1;i++){
            vec2 g=vec2(i, j);
            vec2 o=hash22(n+g);
	        vec2 r=g-f+(.5+.5*sin(20.*time+2.*PI*o));
		    float d=dot(r, r);
            if(d<m.x) m=vec3(d, o);
        }
    }

    return vec2(sqrt(m.x), m.y+m.z);
}

//distance from point to line
float d2l(float t, vec2 p){
    return abs(cos(t)*p.y-sin(t)*p.x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    //min and max angle change between crosshatch level
    float minChg=1./float(N+1);
    float maxChg=1./float(N);

    //randomly offset coord
    vec2 coord=fragCoord+hash22(fragCoord)-.5;
    vec2 uv=coord/iResolution.xy;

    //input tex luminance
    vec4 col=texture(iChannel0, uv);
    float lum=LUM(col.rgb);

    //voronoi cell info(rand seed)
    vec2 p=fragCoord/max(iResolution.x,iResolution.y);
    vec2 c=voronoi(VN_SCL*p);

    //generalized crosshatch procedure
    float theta, hatch=1.;
    for(int i=0;i<N;i++){
        float bound=1.-float(i+1)/float(N+1);
        if(lum<bound){
            if(i==0) theta=2.*PI*hash11(c.y);
            else theta+=PI*mix(minChg, maxChg, hash11(c.y+float(i)));

            float dist=d2l(theta, coord);
            float repeat=1.-abs(LN_SZ-2.*mod(dist, LN_SZ))/LN_SZ;
            float line=smoothstep(.5, 1., repeat);
            hatch-=line;
        }
    }
    fragColor=clamp(col*hatch, .0, 1.);

    vec2 sum=vec2(0);
    for(int i=0;i<3;i++){
        for(int j=0;j<3;j++){
            vec2 offsetUV=vec2(i-1, j-1)/iResolution.xy;
            vec3 col=texture(iChannel0, uv+offsetUV).rgb;
            float lum=LUM(col.rgb);
            sum+=lum*vec2(sobelX[i][j], sobelY[i][j]);
        }
    }
    fragColor.a=smoothstep(.5, 1., length(sum));
}

#define PI 3.1415927

//hash from dave hoskins
//https://www.shadertoy.com/view/4djSRW
float hash11(float p){
    p=fract(p*.1031);
    p*=p+33.33;
    return fract(2.*p*p);
}
vec2 hash22(vec2 p){
	vec3 p3=fract(p.xyx*vec3(.1031, .1030, .0973));
    p3+=dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}
