void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 a_n_pix_cor_nor = fragCoord/iResolution.xy;

    float n_dist = abs(a_n_pix_cor_nor.y - 0.5);
    //float n_dist = 0.5 - a_n_pix_cor_nor.y;

    float a_n_dist_modified[] = float[](
        step(0.2, n_dist), 
        smoothstep(0.2, 0.25, n_dist), 
        n_dist, 
        mod(n_dist, 0.2)/0.2,
        sqrt(n_dist),
        pow(n_dist,1.0/3.0), 
        n_dist*n_dist*0.5
    );

    for(
        float n_index_norm_a_n_dist_modified = 0.0;
        n_index_norm_a_n_dist_modified < 1.0; 
        n_index_norm_a_n_dist_modified+= (1.0/float(a_n_dist_modified.length()))
    ){
        float n = a_n_dist_modified[
            int(n_index_norm_a_n_dist_modified*float(a_n_dist_modified.length()))
        ];
        if(a_n_pix_cor_nor.x > n_index_norm_a_n_dist_modified){
            fragColor = vec4(n);
        }
    }

}
