//const float n_tau = 6.28318530717958647;
float f_n_difference_to_line_function(
    vec2 a_n_start, 
    vec2 a_n_end, 
    vec2 a_n_pixel_coord_normalized
){
    //remember that math lesson ?
    // y = m*x+q
    float n_delta_y = a_n_end.y - a_n_start.y; 
    float n_delta_x = a_n_end.x - a_n_start.x; 
    float n_m = n_delta_y / n_delta_x;
    float n_q = -(n_m*a_n_start.x) - a_n_start.y;

    float n_x = a_n_pixel_coord_normalized.x;

    float n_y = n_m * n_x + n_q; 

    float n_difference = n_y - a_n_pixel_coord_normalized.y;
    return n_difference;


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 a_n_pixel_pos_normalized = fragCoord.xy / iResolution.xy;
    vec2 a_n_mouse_pos_normalized = iMouse.xy / iResolution.xy;

    float n_brightness = 1.0;

    float n_lines = 2000.0 * a_n_mouse_pos_normalized.x;
    for(float n_i = 1.0; n_i <= n_lines; n_i+=1.0){
        float n_difference_to_line_function = f_n_difference_to_line_function(
            vec2(1.0-(1.0/n_lines)*n_i, 0.0), 
            vec2((1.0/n_lines)*n_i,1.0), 
            a_n_pixel_pos_normalized
        );
        n_brightness *= (n_difference_to_line_function*1000.0*a_n_mouse_pos_normalized.x);
    }
    float n_i = 1.0;


    fragColor = vec4(n_brightness);
}
