#define DIV 64.0
#define TEXTURE_HEIGHT 1024.0

vec2 getCoord(vec2 coord, float num, float digits, float shift) {
    coord += vec2(DIV * mod(floor(num / pow(10.0, digits - shift - 1.0)), 10.0), 0.0);
    return coord / TEXTURE_HEIGHT;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 coord = fragCoord.xy;

    vec2 upc = vec2(fragCoord.x, fragCoord.y - iResolution.y);

    upc = mod(upc, DIV);

    upc.y += DIV * 12.0;

    float num = pow(2.0, floor(sin(iTime) * 16.0 + 16.0));
    float log10 = 0.4342944819;
    float digits = floor(log10 * log(num) + 1.0);

    float current = floor(fragCoord.x / DIV);

    vec4 color = vec4(texture(iChannel0, getCoord(upc, num, digits, current)).r);

    if (fragCoord.x > DIV * digits || fragCoord.y < iResolution.y - DIV)
        color = vec4(0.0);

    fragColor = color;
}
