float random (in vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}


float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(a, b, u.x) +(c - a)* u.y * (1.0 - u.x) +(d - b) * u.x * u.y;
}

#define OCTAVES 8
float fbm (in vec2 st) {
    // Initial values
    float value = 0.0;
    float amplitud = .5;
    float frequency = 0.;
    //
    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitud * noise(st);
        st *= 2.;
        amplitud *= .5;
    }
    return value;
}

vec3 draw_sky(vec2 uv ){
    vec3 c0 = vec3(0.996,0.868,0.297) ; 
    vec3 c1  = vec3(0.996,0.711,0.515) ; 
    vec3 c2 = vec3(0.151,0.694,0.996) ; 
    float h1 = 0.05; 
    if(uv.y <  h1 ){
        if(uv.y <  0.5*h1 ){
        float gradient = (uv.y - h1 ) / (h1) ; 
        return mix(c1 , c0  , gradient)  ;  
        }
        else
        {
        float gradient = (uv.y - h1 ) / (h1) ; 
        return mix(c1 , c0  , gradient)  ;     
        }
    }else{
        float gradient = (uv.y - h1 ) / (h1) ; 
        return mix(c1 , c2  , gradient)  ;       
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

	vec3 sky  = draw_sky(st) ; 
    vec3 white = vec3(0.971,1.000,0.785) ; 
    float r = fbm(st*4.0);
    vec3 color = mix(sky,white,r) ; 

    // Output to screen
    fragColor = vec4(color,1.0);
}
