float f_n_dist_square(vec2 o, vec2 o_2){
    vec2 o_delta = abs(o - o_2);

    return max(o_delta.x, o_delta.y);

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;


    vec2 o_scale = vec2(0.1);

    vec2 o_fragCoord_scaled = (fragCoord.xy*o_scale);
    vec2 o_iResolution_scaled = (iResolution.xy * o_scale);
    vec2 o_fragCoord_scaled_floor = floor(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_floor = floor(o_iResolution_scaled);
    vec2 o_fragCoord_scaled_fract = fract(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_fract = fract(o_iResolution_scaled);

    vec2 o_trpino_scaled = (o_fragCoord_scaled_floor.xy - o_iResolution_scaled_floor.xy*0.5)/ o_iResolution_scaled_floor.y;
    vec2 o_trpino_scaled_nooffset = (o_fragCoord_scaled_floor.xy)/ o_iResolution_scaled_floor.xy;

    float n_index = o_fragCoord_scaled_floor.y * o_iResolution_scaled_floor.x + o_fragCoord_scaled_floor.x;
    float n_index_nor = n_index / (o_iResolution_scaled_floor.x * o_iResolution_scaled_floor.y);

    float n_amp_freqdomain = texture(iChannel0, vec2(o_trpino_scaled_nooffset.x, 0.0)).x;
    float n_amp_timedomain = texture(iChannel0, vec2(o_trpino_scaled_nooffset.x, 1.0)).x;
    //n_amp_freqdomain = abs(n_amp_freqdomain - 0.5);
    float n_t = n_index_nor*n_amp_timedomain*1.+0.5;
    float n_dist_square_tmod = fract(f_n_dist_square(o_fragCoord_scaled_fract, vec2(0.5))+n_t);

    float n_dist_square_scaled_fract = f_n_dist_square(o_fragCoord_scaled_fract, vec2(0.5));
    //n_dist_square_scaled_fract += texture(iChannel0, vec2(0.0,n_index_nor)).x;
    //n_dist_square_scaled_fract += n_amp_freqdomain;

    n_dist_square_scaled_fract = fract(n_dist_square_scaled_fract);
    fragColor = vec4(n_dist_square_tmod, n_index_nor,n_dist_square_scaled_fract,1.);

    //fragColor *= n_amp_freqdomain;

}
