#define LOOPTIMES 8

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 6.0 * (fragCoord / iResolution.xy /** 2.0 - 1.0*/) / vec2(iResolution.y / iResolution.x, 1);

    for(int i = 1; i < LOOPTIMES + 1; i++)
    {
        float index = float(i);
        uv += vec2(0.7 / index * sin(index * uv.y + iTime + 0.3) + 0.8, 0.4 / index * sin(index * uv.x + iTime + 0.3) + 0.6);
    }

    vec3 col = vec3(0.5 * sin(uv.x) + 0.5, 0.5 * sin(uv.y) + 0.5, sin(uv.x + uv.y));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
