// https://www.shadertoy.com/view/DdSyDD
// playing around with projection - 2023 by jt

vec2 div_z(vec3 v)
{
    return vec2(v) / v.z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 2.0 * fragCoord/iResolution.xy - 1.0;

    p *= 2.0;

    float g = 0.1 * cos(iTime);
    float h = 0.1 * sin(iTime);
    mat3 m = mat3
    (
        vec3(1,0,g),
        vec3(0,1,h),
        vec3(0,0,1)
    );
/*
    // fix horizontal
    mat3 m = mat3
    (
        vec3(1.0-g,0,g),
        vec3(0,1,0),
        vec3(0,0,1)
    );
*/
/*
    // fix vertical
    mat3 m = mat3
    (
        vec3(1,0,0),
        vec3(0,1.0-h,h),
        vec3(0,0,1)
    );
*/

    p = div_z(m * vec3(p,1));

    //fragColor = vec4(vec3(fract(p), 0.0), 1.0);
    fragColor = texture(iChannel0, fract(p));
}
