void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv *= 3.;
    vec3 z = vec3(uv,uv.x);

    z.x *= iResolution.x/iResolution.y;
    float a = 27.762;
    z.xz *= mat2(cos(a),-sin(a),sin(a),cos(a));

    z.z -= .7 * sin(iTime);
    vec3 c = z;

    vec3 col;
    for(float i =0.;i<200.;i++){
        float r = length(z);
        float theta = atan(z.y,z.x);
        float phi = atan(r,z.z);
        float p = 10.;
        r = pow(r,p);
        theta *= p;
        phi *= p;
        z = r * vec3(cos(theta) * cos(phi),sin(theta) * cos(phi),sin(phi));
        z+= c;
        if(length(z) < 2.)col.r = i/200.;
    }


    fragColor = vec4(col,1.0);
}
