void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float curve =iTime*-10.0f+uv.y*10.0f;
    uv.y *= 2.0f;
    float widthFlame = 0.1f;
    widthFlame  *= (1.0f-uv.y);
    float line = uv.x > (0.5f-widthFlame)+sin(curve)*-1.0f*0.05f && uv.x < (0.5f+widthFlame)+sin(curve)*-1.0f*0.05f ? 1.0f : 0.0f;
    vec3 myColor = vec3(1.0f, smoothstep(0.4f, 0.8f, uv.y), 0.0f)*line;
    fragColor = vec4(myColor.x,myColor.y,myColor.z,1.0);
}
