vec2[] all = vec2[] (
    vec2(200,200),
    vec2(400,200)
);
float id(vec2 a, vec2 b){
    return 1./sqrt(length((a-b)*(a-b)))*40.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float uv = 0.;
    uv+=id(fragCoord.xy, iMouse.xy);
    for(int i = 0; i < all.length(); i++){
        uv+=id(fragCoord.xy,all[i]);
    }

    uv= round(uv*10.)*0.1;
    // Output to screen
    fragColor = vec4(uv,uv*0.5,0,1.0);
}
