vec3 red = vec3(1.0,0.0,0.0);
vec3 blue = vec3(0.0,0.0,1.0);
vec3 purple = vec3(0.4,0.0,1.0);
vec3 green = vec3(0.0,1.0,0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float smoth3 =sin(uv.x);
    float smoth = (sin(uv.x * 1.3) * cos(uv.x)) * 1.0;
    float smoth2 = sin(uv.y);
    // Time varying pixel color
    vec3 col = mix(mix(red, blue,smoth2), mix(purple, green, smoth3),smoth);
    vec3 colr = mix(purple, green, smoth);

    fragColor = vec4(col,1.0);
}
