vec2 skew(vec2 v) {
    return v*mat2(
        1., 0.,
        -0.5, 0.86603 // approximately sqrt(0.75)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv2 = mix(uv, skew(uv), cos(iTime)/2.+0.5);
    vec3 col = vec3(fract(uv2*2.), 1);

    fragColor = vec4(col, 1);
}
