void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = (fragCoord.xy / iResolution.xy) * 3.5 - vec2(2.2, 1.5);
    vec2 z = vec2(.0, 0.0);
    float itimemod = mod(iTime,20.);
    float itimemod2 = mod(iTime,60.);


    float time = itimemod < 10. ? itimemod : 20.-itimemod;

    float t2 = 1.-pow(.5,time);

    c *= 1. - t2;
    if (itimemod2 < 20.) {
        c.x -= t2 * 1.4846942;
    } else if (itimemod2 < 40.) {
        c.x -= t2 * .77;
        c.y += t2 * .1;
    } else  {
        c.x -= t2 * .167;
        c.y += t2 * 1.0425;
    }
    float iter = 0.0;
    float max_iter = max(100., 200.0 * t2);

    for (int i = 0; i < int(max_iter); i++) {
        vec2 z_new = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        if (length(z_new) > 2.0) {
            iter = float(i) / max_iter;
            break;
        }
        z = z_new;
    }


    fragColor = vec4(iter*4.2, sin(iter-.5), cos(iter*24.9), 1.0);
}
