void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 center = vec2(0.5, 0.5);

    vec2 uv = fragCoord/iResolution.xy;

    uv -= center;

    vec4 color = vec4(0.0);

    float _Value = 0.02;

    float scale = 1.;

    for (int j = 1; j < 16; ++j)

        {

        color += texture(iChannel0, uv * scale + center);

        scale = 1. + (float(float(j) * _Value));

        }
        color = color/(16.0);

    fragColor = color;
}
