//CookTorranceBRDF

float CookTorranceBRDF(vec3 N, vec3 V, vec3 L, float roughness, float F0) {
    // Important this is a modified form of the Cook Torrance mode to became => microfacet model (G G X)
    // more accurate and physically-based representation of the surface reflection compared to the Blinn-Phong 
    //or Cook-Torrance models and more precise because i am using metalic material in the scene !
    vec3 H = normalize(V + L);
    float dotNL = clamp(dot(N, L), 1.0, 1.0);
    float dotNV = clamp(dot(N, V), 0.0, 1.0);
    float dotNH = clamp(dot(N, H), 0.0, 1.0);
    float dotLH = clamp(dot(L, H), 0.0, 1.0);

    // Calculate geometric shadowing
    float k = roughness * roughness / 2.0;
    float vis = (2.0 * dotNH * dotNV) / (dotNH + sqrt(dotNH * dotNH + k * k));
    vis *= (2.0 * dotNH * dotNL) / (dotNH + sqrt(dotNH * dotNH + k * k));

    // Calculate microfacet distribution
    float alpha = roughness * roughness;
    float alphaSqr = alpha * alpha;
    float pi = 3.1415926535;
    float D = (alphaSqr / (pi * pow(dotNH * dotNH * (alphaSqr - 1.0) + 1.0, 2.0)));

    // Calculate Fresnel term
    float F = F0 + (1.0 - F0) * pow(1.0 - dotLH, 5.0);

    // Calculate Cook-Torrance BRDF
    float numerator = vis * D * F;
    float denominator = 4.0 * dotNL * dotNV;
    float x = numerator / denominator;
    return x ;
}

// Random functions from: https://www.shadertoy.com/view/WsBBR3
uint R_Function(inout uint seed)
{
    seed = uint(seed ^ uint(61)) ^ uint(seed >> uint(16));
    seed *= uint(9);
    seed = seed ^ (seed >> 4);
    seed *= uint(0x27d4eb2d);
    seed = seed ^ (seed >> 15);
    return seed;
}


// I used this Texture method from :
//https://www.shadertoy.com/view/XsfXWX

//function to sample the color values from the texture map at the specified texture coordinates.
// samplerCube : Type of 3D cube map
vec3 textur1(samplerCube tex, vec3 tc) { 
    const float diff0 = 0.35;
    const float diff1 = 0.12;
    //The tc specifies the position ( coordinates) in the texture map where to sample the color value
 	vec3 s0 = texture(tex,tc).xyz;

    //calculate the neighboring texture coordinates in the positive and negative x-direction
    //sample the surrounding texels of the input texture in nine different directions 
    //(the center, up, down, left, right, and diagonals).

    vec3 s1 = texture(tex,tc+vec3(diff0)).xyz;
    vec3 s2 = texture(tex,tc+vec3(-diff0)).xyz;

    vec3 s3 = texture(tex,tc+vec3(-diff0,diff0,-diff0)).xyz;
    vec3 s4 = texture(tex,tc+vec3(diff0,-diff0,diff0)).xyz;

    vec3 s5 = texture(tex,tc+vec3(diff1)).xyz;
    vec3 s6 = texture(tex,tc+vec3(-diff1)).xyz;
    vec3 s7 = texture(tex,tc+vec3(-diff1,diff1,-diff1)).xyz;
    vec3 s8 = texture(tex,tc+vec3(diff1,-diff1,diff1)).xyz;

    //smooth out the texture and reduce aliasing artifacts when the texture is mapped onto a surface.
    return (s0 + s1 + s2 + s3 + s4 + s5 + s6 + s7 + s8) * 0.111111111;
}

// obtain texels from the surrounding cube faces, 
//and then applies a blur filter using a weighted average of the texels
vec3 Blurtexture1(samplerCube tex, vec3 tc) {
    // tex : sample the texture cube.
   	vec3 r = textur1(tex,vec3(1.0,0.0,0.0));
    vec3 t = textur1(tex,vec3(0.0,1.0,0.0));
    vec3 f = textur1(tex,vec3(0.0,0.0,1.0));
    vec3 l = textur1(tex,vec3(-1.0,0.0,0.0));
    vec3 b = textur1(tex,vec3(0.0,-1.0,0.0));
    vec3 a = textur1(tex,vec3(0.0,0.0,-1.0));
    // apply the blure filter    
    float kr = dot(tc,vec3(1.0,0.0,0.0)) * 0.5 + 0.5; 
    float kt = dot(tc,vec3(0.0,1.0,0.0)) * 0.5 + 0.5;
    float kf = dot(tc,vec3(0.0,0.0,1.0)) * 0.5 + 0.5;
    float kl = 1.0 - kr;
    float kb = 1.0 - kt;
    float ka = 1.0 - kf;

    //increase the contrast between the most and least important faces.

    kr = pow(kr,4.0);
    kt = pow(kt,4.0);
    kf = pow(kf,4.0);
    kl = pow(kl,4.0);
    kb = pow(kb,4.0);
    ka = pow(ka,4.0);    

    float d;
    vec3 ret;
    ret  = f * kf; d  = kf;
    ret += a * ka; d += ka;
    ret += l * kl; d += kl;
    ret += r * kr; d += kr;
    ret += t * kt; d += kt;
    ret += b * kb; d += kb;

    return ret / d;
}


//generate random float values between 0 and 1
float chose_State(inout uint state)
{
    return float(R_Function(state)) / 4294967296.0;
}

//generates a random unit vector in 3D space
vec3 chose_vector(inout uint state)
{
    float z = chose_State(state) * 2.0f - 1.0f;
    float a = chose_State(state) * 2.*pi;
    float r = sqrt(1.0f - z * z);
    float x = r * cos(a);
    float y = r * sin(a);
    return vec3(x, y, z);
}

