#define isKeyHeld(k)  (texelFetch(iChannel1, ivec2(k,0), 0).x > 0.)

Ray Camera(vec2 fragCoord, out vec3 eye, out vec2 data2)
{   
    // *** Ray generation ***

    // camera position
    eye = texelFetch(iChannel0, ivec2(0,0), 0).yzw+EyeStartPosition;

    // spherical coordinates
    data2 = texelFetch(iChannel0, ivec2(1,0), 0).zw;	
    vec2 uv	= abs(data2);

    //mouse held or was held last frame
   	if(iMouse.z>0. || data2.x >= 0.)	
        uv += (abs(iMouse.zw)-abs(iMouse.xy))*0.01;

    vec3 w = vec3(cos(uv.x)*cos(-uv.y), sin(-uv.y), sin(uv.x)*cos(-uv.y));
    vec3 u = normalize(cross(vec3(0,1,0),w));
	vec3 v = cross(w,u);

    vec2 px = (fragCoord/iResolution.xy*2.-1.)*1.*normalize(iResolution.xy);

    // Keyboard and mouse handling:
	float speed = 0.2;
    if (isKeyHeld(KeyLeft )) eye -= u*speed;
    if (isKeyHeld(KeyRight)) eye += u*speed;
    if (isKeyHeld(KeyUp   )) eye += w*speed;
    if (isKeyHeld(KeyDown )) eye -= w*speed;

    if(iMouse.z>=0.)		//mouse held
        data2 = abs(data2.xy);
	else if(data2.x >= 0.)	//mouse released
        data2 = -mod(uv,2.*pi);

    // Ray generation
    return Ray(eye,							//V
               0.5,							//minT
               normalize(w+px.x*u+px.y*v),	//P
               500.);						//maxT
}

// Plane intersection
TraceResult intersectPlane(Ray ray, Plane plane) 
{   
    //Calculate the distance along the ray at which it intersects the plane
    float t = dot(plane.q0-ray.p0,plane.n)/dot(ray.v,plane.n);

    //Return a TraceResult struct that contains the normal of the plane 
    //the distance at which the ray intersects the plane, the material of the plane,
    //and whether or not the plane is textured.
    return TraceResult(plane.n, t, plane.mat, plane.textured);
}

// Sphere intersection
TraceResult intersectSphere(Ray ray, Sphere sp) 
{   
    // Vector from the ray origin to the center of the sphere
    vec3 p0c = ray.p0 - sp.c;  
    float a = dot(ray.v, ray.v); 
    float b = 2.0 * dot(p0c, ray.v); 

    // Distance squared between ray origin and sphere center
    float c = dot(p0c,p0c) - sp.r*sp.r; 

    float discriminant = b*b - 4.0*a*c; 

    // If the discriminant is negative, no intersection
    if(discriminant < 0.0)

        // Return a TraceResult with black color and no intersection
        return TraceResult(vec3(0), ray.tmax, colors[5], false); 

    float sqd = sqrt(discriminant); 
    float numerator = -b - sqd;
    if(numerator < 0.0)

        // Select the smaller root if both roots are positive
        numerator = -b + sqd;

    // Compute the intersection distance along the ray
    float t = 0.5 * numerator / a; 

    // Compute the intersection point
    vec3 p = ray.p0 + t * ray.v; 

    // Compute the normal at the intersection point
    vec3 normal = normalize(p - sp.c); 

    // Return a TraceResult with the normal, intersection distance, material, and texture information
    return TraceResult(normal, t, sp.mat, sp.textured); 
}



 // MÃ¶ller-Trumbore Triangle intersection
TraceResult intersectTriangle(Ray ray, Triangle trg)
{   
    // Compute edge1 of the triangle by subtracting the endpoint b from a
    vec3 edge1 = trg.b - trg.a;
    // Compute edge2 of the triangle by subtracting the endpoint c from a
    vec3 edge2 = trg.c - trg.a;     
    vec3 pvec = cross(ray.v, edge2);  
    float det = dot(edge1, pvec);   

    if (det > -EPSILON && det < EPSILON) 

        // Return no intersection if it is close to zero
        return TraceResult(vec3(0), ray.tmax, colors[5], false);   

    float inv_det = 1.0 / det;   
    vec3 tvec = ray.p0 - trg.a;   
    float u = dot(tvec, pvec) * inv_det; 

     // Check if the intersection point lies outside the triangle
    if (u < 0.0 || u > 1.0) 

        // Return no intersection if it is outside the triangle
        return TraceResult(vec3(0), ray.tmax, colors[5], false);   

    vec3 qvec = cross(tvec, edge1);   
    float v = dot(ray.v, qvec) * inv_det; 

     // Check if the intersection point lies outside the triangle
    if (v < 0.0 || u + v > 1.0)   

         // Return no intersection if it is outside the triangle
        return TraceResult(vec3(0), ray.tmax, colors[5], false); 

     // Compute the distance along the ray to the intersection point
    float t = dot(edge2, qvec) * inv_det;   

     // Check if the intersection point lies in front of the starting point of the ray
    if (t > EPSILON) { 

         // Compute the intersection point
        vec3 p = ray.p0 + t * ray.v;  
        vec3 normal = normalize(cross(edge1, edge2));

        // Flip the normal vector if it is facing away from the ray
        if (dot(normal, ray.v) > 0.0) normal = -normal;  

        // Return the intersection point and its properties
        return TraceResult(normal, t, trg.mat, trg.textured);   
    }
     // Return no intersection if the intersection point lies behind the starting point of the ray
    return TraceResult(vec3(0), ray.tmax, colors[5], false);  
}

TraceResult intersectCylinder(Ray ray, Cylinder cyl)
{   
    // Compute the vector OC from the ray origin to the cylinder position
    vec3 oc = (ray.p0 - cyl.position);

    // Compute the coefficients of the quadratic equation
    float a = dot(ray.v, ray.v) - dot(ray.v, cyl.position)*dot(ray.v, cyl.position);
    float b = float(2)*dot(ray.v, oc) -(dot(ray.v,cyl.position)*dot(oc,cyl.position));
    float c = dot(oc, oc) - dot(oc, cyl.position)*dot(oc, cyl.position) - (cyl.radius*cyl.radius);

    // Compute the discriminant of the quadratic equation
    float delta = b*b - float(4)*(a * c);

    // Initialize the TraceResult structure with default values
    TraceResult res;
    res.t = 1000.f;
    res.textured = false;

    // If the discriminant is positive, the ray intersects the cylinder
    if (delta > float(0))
    {   
        // Compute the two intersection points
        float t1 = (-b - sqrt(delta)) / (2. * a);
        float t2 = (-b + sqrt(delta)) / (2. * a);

        // Compute the coordinates of the two intersection points
        vec3 p1 = ray.p0 + t1 * ray.v;
        vec3 p2 = ray.p0 + t2 * ray.v;

        // Test which intersection point is closest to the ray origin and within the height of the cylinder
        if (t1 < t2)
        {
            if (p1.y >= cyl.position.y && p1.y <= cyl.position.y + cyl.height)
            {
                res.t = t1;
                res.n = normalize(p1 - vec3(cyl.position.x, p1.y, cyl.position.z));
            }
            else if (p2.y >= cyl.position.y && p2.y <= cyl.position.y + cyl.height)
            {
                res.t = t2;
                res.n = normalize(p2 - vec3(cyl.position.x, p2.y, cyl.position.z));
            }
        }
        else
        {
            if (p2.y >= cyl.position.y && p2.y <= cyl.position.y + cyl.height)
            {
                res.t = t2;
                res.n = normalize(p2 - vec3(cyl.position.x, p2.y, cyl.position.z));
            }
            else if (p1.y >= cyl.position.y && p1.y <= cyl.position.y + cyl.height)
            {
                res.t = t1;
                res.n = normalize(p1 - vec3(cyl.position.x, p1.y, cyl.position.z));
            }
        }
    }

    // If the distance of the intersection is still the default value, there was no intersection
    if (res.t == float(100))
    {
        return res;
    }

    // Set the material of the cylinder in the TraceResult structure
    res.mat = cyl.color;
    res.textured = true;

    // Return the TraceResult structure
    return res;
}



// Function to calculate the color and other properties of the closest object intersected by a ray.
TraceResult raycast(Ray ray)
{
    // Initialize the TraceResult object with a default value.
    TraceResult res = TraceResult(vec3(0), ray.tmax, colors[5], false);

    // Iterate over all spheres in the scene and check for intersections with the ray.
    for(int i = 0; i < spheresNo; ++i)
    {   // Calculate the intersection of the ray and the sphere.
        TraceResult res2 = intersectSphere(ray, spheres[i]); 

        // If the intersection is valid and closer than the previous intersection, update the TraceResult object.
        if(res2.t < ray.tmax && res2.t > ray.tmin && res2.t < res.t){
            res = res2;
        }
    }

    // Iterate over all planes in the scene and check for intersections with the ray.
    for(int i = 0; i < planeNo; ++i)
    {   // Calculate the intersection of the ray and the plane.
        TraceResult res2 = intersectPlane(ray, planes[i]); 

        // If the intersection is valid and closer than the previous intersection, update the TraceResult object.
        if(res2.t < ray.tmax && res2.t > ray.tmin && res2.t < res.t){
            res = res2;
        }
    }

    // Iterate over all triangles in the scene and check for intersections with the ray.
    for(int i = 0; i < trianglesNo; ++i)
    {
        TraceResult res2 = intersectTriangle(ray, triangles[i]); // Calculate the intersection of the ray and the triangle.
        // If the intersection is valid and closer than the previous intersection, update the TraceResult object.
        if(res2.t < ray.tmax && res2.t > ray.tmin && res2.t < res.t){
            res = res2;
        }
    }

    // Iterate over all cylinders in the scene and check for intersections with the ray.
    for(int i = 0; i < cylinderNo; ++i)
    {   
        // Calculate the intersection of the ray and the cylinder.
        TraceResult res2 = intersectCylinder(ray, cylinders[i]); 

        // If the intersection is valid and closer than the previous intersection, update the TraceResult object.
        if(res2.t < ray.tmax && res2.t > ray.tmin && res2.t < res.t){
            res = res2;
        }
    }

    // The function returns a TraceResult objectnwith the closest intersection and that contains the color, normal
    return res;
}






// Function to calculate the color of a pixel based on ray tracing and shading
vec4 hitColor(Ray ray, TraceResult tr, vec3 lightPos)
{   
     // Get the material of the object that was hit
    Material mat = tr.mat;

    // Calculate the point of intersection between the ray and the object
    vec3 p = ray.p0 + ray.v * tr.t; 
    // Get the surface normal at the point of intersection
    vec3 n = tr.n; 

    // Calculate the vector from the point of intersection to the light source
    vec3 l = lightPos - p; 

    // Get the distance between the point of intersection and the light source
    float lightDist = length(l); 
    // Normalize the direction vector to the light source
    l /= lightDist; 

    // Create a ray to check if the point is in shadow
    Ray shadowray = Ray(p + 0.001 * n, 0.001, l, lightDist); 

    // Cast the shadow ray to check if the point is in shadow
    TraceResult res = raycast(shadowray); 

    // Get the view direction
    vec3 v = -ray.v; 

    // Normalize the direction vector to the light source
    l = normalize(lightPos - p); 

    // Get the metalness of the material
    float metallic = mat.metalness; 

    // Get the roughness of the material
    float roughness = mat.roughness; 

    // Calculate the fresnel power based on the metalness of the material
    float fresnel_pow = mix(3., 3.5, mat.metalness); 

    // Modify the color of the material
    vec3 color_mod = mat.color + 0.5; 

    // Get the color of the light source
    vec3 light_color = pow(texture(iChannel2, vec3(1.0,0.0,0.0)).xyz * 1.2, vec3(2.2)); 

    // Calculate the diffuse and reflection components of the scence based on scence scence lighting (IBL)
    // and aplly the tesxture effects. ( bluring)
    // tecture coordinates n:texture coordinates , iChannel2 : sampler Cube map
    vec3 ibl_diffuse = pow(Blurtexture1(iChannel2, n), vec3(2.2));
    vec3 ibl_reflection = pow(Blurtexture1(iChannel2, reflect(ray.v, n)), vec3(2.2));

    // Calculate the fresnel effect
    float fresnel = max(1.0 - dot(n, -ray.v), 0.0);
    fresnel = pow(fresnel, fresnel_pow);

    // Calculate the reflection component of the scene
    vec3 refl = pow(texture(iChannel2, reflect(ray.v, n)).xyz, vec3(2.2));
    refl = mix(refl, ibl_reflection, (1.0 - fresnel) * roughness);
    refl = mix(refl, ibl_reflection, roughness);

    // Calculate the specular component of scene
    float power = 1.0 / max(roughness * 0.4, 0.01);

    // Applying CookTorranceBRDF : surface reflects light as a combination of the diffuse reflection 
    //of rough surfaces with the specular reflection of shiny surfaces
    vec3 spec = mat.color * CookTorranceBRDF(n, -ray.v, lightPos, roughness * 0.7, 0.2);
    refl -= spec;

    // Calculate shadow effect
    float sh = 1.;

    //If the point is in shadow
    if (!(res.t == shadowray.tmax || mat.emission != vec3(0.) || res.mat.emission != vec3(0.))) 
    {
        sh = 0. ; 
    }

    // diffuse
    vec3 diffuse = ibl_diffuse * dot(n,l) * sh;
    diffuse = mix(diffuse * color_mod, refl, fresnel); 

    //compute refraction component using ior reference
    vec3 refr = vec3(0.0);
    float eta = mat.ior;
    if (eta > 0.0) {
        float cosTheta1 = dot(n, -ray.v);
        float sinTheta1 = sqrt(1.0 - cosTheta1 * cosTheta1);
        float sinTheta2 = sinTheta1 / eta;
        if (sinTheta2 < 1.0) {
            float cosTheta2 = sqrt(1.0 - sinTheta2 * sinTheta2);
            vec3 t = (ray.v / eta) + ((cosTheta1 / eta - cosTheta2) * n);
            Ray refray = Ray(p - 0.001 * n, 0.001, t, ray.tmax);
            TraceResult refr_res = raycast(refray);
            refr = pow(texture(iChannel2, refray.v).xyz, vec3(2.2));

        }
    }



    vec3 colorfinal = mix(diffuse,refl*color_mod, metallic)+spec;

    vec3 specular;
    if(dot(n,l) < 0.)
    {
        specular = vec3(0);
    }
    else
    {
        specular = vec3(30, 30, 30);
        float specdot = dot(reflect(l, n), v);
        specular = l * specular * pow(max(0.0, specdot), 100.);
    }

    // Adding textures 
    if(tr.textured) 
    {
        vec3 mattex = texture(iChannel3, p.xy).rgb;
        mattex *= 0.8;
        colorfinal+=mattex;
    }

    vec3 col = diffuse + (colorfinal + specular*mat.metalness) * max(dot(n,l), 0.);

    // In case is a light source
    if(mat.emission != vec3(0.)) 
    { 
        return vec4(col + clamp(mat.emission, 0., 1.), 1.);
    }


    return vec4(col, 1.);
}


vec4 Refraction_Depth_Max(Ray ray, TraceResult tr, vec3 lightPos, int max_depth)
{   
    //Initialize
    // Get the material of the surface that was hit
    Material mat = tr.mat ;

    // Calculate the point of intersection between the ray and the surface
    vec3 p = ray.p0 + ray.v * tr.t;

    // Initialize variables for the refraction ray's origin, direction,
    // and index of refraction
    vec3 refractionRayOrigin;
    vec3 refractionDir;
    float ior = mat.ior;


    // Calculate the amount of light that reflects off the surface : fresnel
    float reflectance = mix(3., 3.5, mat.metalness); 

    // Check if the ray is outside the surface
    bool outside = dot(ray.v, tr.n) < 0.;

    // Initialize the color of the refracted light
    vec4 refractionColor = vec4(0.);

    // If the surface is not a perfect mirror, calculate the refracted light
    if (reflectance < 1.)
    {
        // Calculate the origin and direction of the refracted ray
        refractionRayOrigin = p - 0.001;
        refractionDir = refract(p, -tr.n, 1.0 / ior);

        // Create the refracted ray and trace it to find the next intersection
        Ray refractionRay = Ray(refractionRayOrigin, 0.001, refractionDir, 500.);
        TraceResult refractionRes = raycast(refractionRay);

        // Calculate the point of intersection of the refracted ray
        vec3 refractionP = refractionRay.p0 + refractionRay.v * refractionRes.t;

        // Add the color of the refracted light to the total color
        refractionColor += (1. - reflectance) * hitColor(refractionRay, refractionRes, refractionP);
    }

    // Calculate the color of the light that is reflected directly from the surface
    //vec4 f_col =  hitColor(ray, tr, lightPos) ;
    vec4 f_col =  vec4(0.) ;

    // Calculate the direction of the reflected light and create a new ray
    vec3 l = lightPos - p;
    vec3 R = reflect(-l, tr.n);
    Ray refray = Ray(p + 0.001 * tr.n, 0.001, R, 500.);

    // Trace the reflected ray to find the next intersection
    TraceResult refres = raycast(refray);

    // Calculate the point of intersection of the reflected ray
    p = refray.p0 + refray.v * refres.t;

    // Initialize a counter for the depth of the recursion
    int counter = 0;

    // Initialize a variable for the surface normal
    vec3 normal;

    // Loop until the maximum recursion depth is reached
    while (counter < max_depth)
    {
        // If the reflected ray does not intersect any surface, exit the loop
        if(refray.tmax <= refres.t)
        {
            break;
        }

        // Get the index of refraction and whether the ray is outside the surface
        ior = mat.ior;
        outside = dot(refray.v, refres.n) < 0.;

        // If the ray is outside the surface, use the surface normal;
        // otherwise, use the negative of the surface

        if (outside == true)
        {
            normal = refres.n;
            reflectance = mix(3., 3.5, mat.metalness); 
        }
        else
        {
            normal = -refres.n;
            reflectance = mix(3., 3.5, mat.metalness); 
        }
        refractionColor = vec4(0.);

        if (reflectance < 1.)
        {
            if (outside == true)
            {
                refractionRayOrigin = p - 0.001;
                refractionDir = refract(p, -tr.n, 1.0 / ior);
            }
            else
            {
                refractionRayOrigin = p + 0.001;
                refractionDir = refract(p, tr.n, ior / 1.0);
            }
            Ray refractionRay = Ray(refractionRayOrigin, 0.001, refractionDir, 500.);

            TraceResult refractionRes = raycast(refractionRay);

            vec3 refractionP = refractionRay.p0 + refractionRay.v * refractionRes.t;

            refractionColor += (1. - reflectance) * hitColor(refractionRay, refractionRes, refractionP);
        }

        f_col += reflectance*hitColor(ray, tr, lightPos);

        p = refray.p0 + refray.v * refres.t;
        l = lightPos - p;
        R = reflect(-l, normal);
        refray = Ray(p + 0.001 * tr.n, 0.001, R, 500.);
        refres = raycast(refray);
        counter += 1;
    }
    return f_col;
}



vec4 missColor(Ray ray)
{
    return vec4(texture(iChannel2, ray.v).xyz, 1.);
}

// Add light from other light sources in the scean
vec4 Extra_Light_sources(Ray ray, TraceResult triang) 
{
    vec4 color;
    for(int i = 0; i < spheresNo; ++i)
    {
        if(spheres[i].mat.emission!=vec3(0.) && spheres[i].c.y > 0.)
        {
            color += hitColor(ray, triang, spheres[i].c);
        }
    }
    for(int i = 0; i < trianglesNo; ++i)
    {
        if(triangles[i].mat.emission!=vec3(0.))
        {
            color += hitColor(ray, triang, triangles[i].a); 
            color += hitColor(ray, triang, triangles[i].b);
            color += hitColor(ray, triang, triangles[i].c);
        }
    }
    return color;
}

// since we can not apply recusion in shaders so applying function with boucles ! 
vec4 Frag_Color_Function(Ray ray, TraceResult ret,vec3 lightposition_1,vec3 lightposition_2)
{
    vec4 Color_Value ;

    // Check if the ray hit anything within its maximum range
    if(ray.tmax <= ret.t){
        // If not, return the color of a "miss" (background color)
        Color_Value = missColor(ray);
    }
    else
    {   
        // If the ray hit something, calculate the color at the hit point using two light sources
        vec4 hitcol1 = hitColor(ray, ret, lightposition_1);
        vec4 hitcol2 = hitColor(ray, ret, lightposition_2);

        // Combine the color from the two light sources
        Color_Value = hitcol1 + hitcol2 ;

        // Add the color contribution from any other light sources (not currently used in this function)
        Color_Value += Extra_Light_sources(ray, ret);
    }

    // Return the final color value
    return Color_Value ;
}

//recursion based on number of bouncing
vec4 Calcul_Ray_Color(vec3 lightposition_1,vec3 lightposition_2,Ray ray, inout uint N_State)
{
    // initialize
    vec4 ret = vec4(0.); 

    // initialize the color multiplier
    vec4 col_multip = vec4(.8); 

    // initialize the ray position
    vec3 rayPos = ray.p0; 

    // initialize the ray direction
    vec3 rayDir = ray.v; 

    // loop over the number of allowed bounces
    for(int i=0; i<=n_bounce; i++) 
    {   
         // trace the ray and get the intersection
        TraceResult trace = raycast(ray);

        // if the ray missed, add in the background color and exit the loop
        if(ray.tmax <= ret.t) 
        {
            ret += vec4(texture(iChannel2, ray.v).xyz, 1.) * col_multip;
            break;
        }

        // update the ray position to the intersection point
        rayPos = (rayPos + rayDir * trace.t) + trace.n *Render_var; 

        if(trace.mat.ior != 0.) // if the material is refractive
        {   
             // calculate the refracted ray
            Ray refRay = Ray(rayPos, ray.tmin, refract(rayDir, trace.n, ray.tmin), ray.tmax); 

             // trace the refracted ray
            TraceResult refRet = raycast(refRay);
            ret += Frag_Color_Function(refRay, refRet,lightposition_1,lightposition_2) * trace.mat.ior * col_multip; // add the color of the refracted ray
        }
        // calculate a new random direction for the reflected ray
        rayDir = normalize(trace.n + chose_vector(N_State)); 

         // add the color of the reflected ray
        ret += Frag_Color_Function(ray, trace,lightposition_1,lightposition_2) * (1.-trace.mat.ior) * col_multip;

        // add in emissive lighting
        ret += vec4(trace.mat.emission, 1.0) * col_multip; 

        col_multip *= vec4(trace.mat.color, 1.); // update the color multiplier

        // create a new ray with the updated position and direction
        Ray newRay = Ray(rayPos, ray.tmin, rayDir, ray.tmax);

        // set the current ray to the new ray
        ray = newRay; 
    }

    // return the final color of the ray
    return ret; 
}




