// Shader by ChatGPT
// Dynamic abstract pattern with smooth color transitions and stacked wild transformations

#ifdef GL_ES
precision mediump float;
#endif

// Simple noise function
float noise(vec2 uv) {
    return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 getColor(float t) {
    return vec3(
        0.5 * sin(t * 2.0) + 0.5,
        0.5 * sin(t * 3.0) + 0.5,
        0.5 * sin(t * 5.0) + 0.5
    );
}

float pattern(vec2 uv, float t) {
    float angle = atan(uv.y, uv.x) + t;
    float radius = length(uv);
    float pattern = sin(radius * 6.0 + angle * 3.0);
    return pattern * 0.5 + 0.5;
}

// First wild transformation
vec2 transformation1(vec2 uv, float t) {
    float scale = 1.0 + sin(t) * 0.5;
    float rotation = sin(t * 0.5) * 3.14159;
    mat2 rotMatrix = mat2(cos(rotation), -sin(rotation), sin(rotation), cos(rotation));
    uv = rotMatrix * uv * scale;
    uv += vec2(sin(t * 0.3) * 0.3, cos(t * 0.7) * 0.3);
    return uv;
}

// Second wild transformation
vec2 transformation2(vec2 uv, float t) {
    uv += vec2(cos(uv.y * 5.0 + t) * 0.1, sin(uv.x * 5.0 + t) * 0.1);
    uv *= 1.0 + sin(noise(uv * 10.0 + t) * 3.14159) * 0.5;
    return uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float t = iTime * 0.5;

    uv = transformation1(uv, t);
    uv = transformation2(uv, t);

    float p = pattern(uv, t);
    vec3 color = getColor(t);
    fragColor = vec4(color * p, 1.0);
}
