float circleH(float x) {
    return sqrt(max(0., 1. - x*x));
}

float sloperH(float x) {
    return sqrt(2.) - x;
}

float raindropSize = .02;

float raindropH(float x) {

    x /= raindropSize;

    float d = 
        x > sqrt(.5) ?
        sloperH(x) :
        circleH(x) ;

    d *= raindropSize * 0.5;

    return d;
}

float repeat = .06;

float repI(float x) {

    // Scroll on the X axis.
    x += iTime;

    return floor(x / repeat);
}

float raindropsH(float x) {

    // Scroll on the X axis.
    x += iTime;

    x = mod(x, repeat) - 0.5 * repeat;

    float h = raindropH(x);

    return h;
}

float offset(float x) {
    return
        2. * sin(13.141 * repI(x))
        + 0.5 * sin(1.9 * repI(x))
    ;
}

#define INSIDE(F, X, Y) (abs(Y) < F(X))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.;
    uv -= 1.;
    uv.x *= iResolution.x / iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0.1);

    if (INSIDE(raindropsH, uv.y, uv.x + offset(uv.y))) {
        col = vec3(0.4, 0.5, 0.9);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
