#ifdef GL_ES
precision mediump float;
#endif

float atan2(vec2 point){
    return mod(degrees(atan(point.y,point.x))+180.,360.);
}
vec2 lengthdir(float len, float dir){
    return vec2(len*cos(radians(dir)),len*sin(radians(dir)));
}
vec2 coord2polar(vec2 center,vec2 point){
    point-=center;
    return vec2(length(point),atan2(point));
}
vec3 polarCosine(vec2 center, vec2 st,vec3 base_color,vec3 shape_color){
    float plot=0.;
	vec2 pst=coord2polar(center,st);
    float len = -0.5*cos(3.*radians(pst.y));
    plot =smoothstep(len+0.01,len,pst.x);
	base_color=mix(base_color,shape_color,plot);
    return base_color;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = ( fragCoord - .5*iResolution.xy )/iResolution.y;
	vec3 color=vec3(0.);
    color=polarCosine(vec2(0.),st,color,vec3(1.));
    fragColor = vec4(color,1.0);
}
