void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y * 2.;

    vec3 col = vec3(0.);
    float index = 0.;

    uv = fract(uv) * 2.;
    index += step(1., uv.y) * 2.;
    index += step(1., uv.x);

    float p = mod(iTime, 2.);
    if (index == 0.) {
      col = vec3(step(p, uv.y));
    } else if (index == 3.) {
      col = vec3(step(p, 1. - fract(uv.y)));
    } else if (index == 2.) {
      col = 1. - vec3(step(p, uv.x));
    } else {
      col = 1. - vec3(step(p, 1. - fract(uv.x)));
    }

    if (mod(iTime, 4.) > 2.) {
        col = 1. - col;
    }

    fragColor = vec4(col,1.0);
}
