void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 m = iMouse.xy / iResolution.xy;
    vec2 uv = fragCoord / iResolution.xy;
    vec3 bg = vec3(1.0, 0.0, 0.0);
    vec3 col = texture(iChannel0, uv).xyz;
    vec3 d = abs(col - vec3(m.y, 1.0, m.y));
    float a = (d.r + d.g + d.b) - m.x;
    a = clamp(a * 4.0, 0.0, 1.0);
    col = mix(bg, col, a);//float(a > m.x));
    if (iMouse.z > 0.0) {
        fragColor = vec4(a);
    } else fragColor = vec4(mix(col, vec3(0.0, 0.0, 1.0), a / 9.0), 1.0);
}
