// teste 1: https://www.shadertoy.com/view/DdVcRK

#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime
#define pardo vec4(10, 4, 0, 0)
#define nuvem vec4(7, 5, 12, 0) / 1.2

void mainImage(out vec4 o, vec2 u) {    
    vec2  r = iResolution.xy; o -= o;
          u = (u - r.xy / 2.) / r.y;

    float i, dd, d = 1., a;
    vec3 B, p, q, D;
    vec4 cor;

    p = vec3(0, 0, cos(t*.5) * 2.1);
    //p.zx *= rot(sin(t * .1) * 11.15);
    //p.zy *= rot(cos(t * .1) * 11.15);

    while (i++ < 50. && d > 1e-2) {
        D = vec3(u, 1.4);

        cor = nuvem;
        a = 1.; d = -.49;
        while (a < 128.){
            q = p,
            q.xy *= rot(a),
            q.yz *= rot(a),
            q = fract(q * a) - .5,
            d += length(q) * .7 / a;
            a += a;
        }

        D.zy *= rot(cos(t * 3.) * .05);
        D.zx *= rot(sin(t * .1) * .5);
        //D.zy *= rot(cos(t * .1) * 11.15);


        // obj
        B.xy = sin(vec2(0, 1.57) + vec2(3.1, 1.7) * t) * .5;
        B.z = sin(t * 1.4) + 2.;

        q = p - B;
        q.xy *= rot(t*5.);
        q.zy *= rot(t*4.);
        q = abs(q);
        dd = dot(q, vec3(.4)) - .1;
        if (2. * dd < d){
            d = 1.5 * dd;
            cor = pardo;
        }

        p += D * d * .4; 
    }

    o = 1. - exp(-1.5 * cor / i + i / 60. - .5);
}
