#define pi 3.1415926

#define EPSILON 0.001
#define DIST_MAX 50.0

float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

// https://iquilezles.org/articles/distfunctions/
float sdSphere( vec3 p, float s )
{
    return length(p) - s;
}

// https://iquilezles.org/articles/distfunctions/
float sdCappedCylinder( vec3 p, float h, float r )
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - vec2(h,r);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// based on https://iquilezles.org/articles/distfunctions/
float line( vec3 p, vec3 a, vec3 b )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

// based on https://www.shadertoy.com/view/3syGzz Limited Repetition SDF by iq
// https://iquilezles.org/articles/distfunctions
vec3 opRepLim( in vec3 p, in vec3 lima, in vec3 limb )
{
    p += 0.5;
    return p - clamp(floor(p),lima,limb);
}

float halfspace(vec3 p)
{
    return p.z;
}

float triangles(vec3 p) // jt
{
    p = (p.x+p.y+p.z < 1.5) ? p : 1.0 - p; // mirror along long diagonal
    if(p.y > p.x && p.z > p.x) p = p.zyx; // reflect
    if(p.z > p.y && p.x > p.y) p = p.xzy; // reflect
    return line(p.xyz, vec3(1,0,0), vec3(0,1,0));
}

float cube(vec3 p) // jt
{
    p = abs(p - 0.5) + 0.5; // mirror symmetry
    p.xy = p.x > p.y ? p.xy : p.yx; // reflect
    p.yz = p.y > p.z ? p.yz : p.zy; // reflect
    return line(p, vec3(1,1,0), vec3(1,1,1));
}

float element(vec3 p) // jt
{
    float d = DIST_MAX;
    d = min(d, triangles(p));
    d = min(d, cube(p));
    return d;
}

float spaceframe(vec3 p, float h) // jt
{
    vec3 lima = vec3(0);
    vec3 limb = vec3(0,0,h);
    vec3 r = opRepLim(p, lima, limb);
    return element(r);
}

// based on https://www.shadertoy.com/view/XtSczV rotational symmetry by iq
// (based on https://www.shadertoy.com/view/XtBcRV Quads Rotate by aiekick
//  based on https://www.shadertoy.com/view/4sS3zz Ellipse - Distance by iq)
float spaceframes(vec3 q, int n, int h)
{
    float an = 2.0*pi/float(n);

    float fa = (atan(q.y,q.x)+an*0.5)/an;
    float ia = floor( fa );

    float sym = an*ia;
    vec3 r = mat3(cos(sym),-sin(sym), 0.0,
                  sin(sym), cos(sym), 0.0,
                  0.0, 0.0, 1.0)*q;

    float d = 4.0 + length(q.xy);
    d = spaceframe(r - vec3(float(n)/5.0,0.0,0.0), float(h));

    return d;
}

bool variant()
{
    return (fract(iTime/10.0) < 0.5);
}

float map(vec3 p)
{
    float d = DIST_MAX;
    d = min(d, halfspace(p));
    //d = min(d, sdSphere(p, 0.1)); // mark origin
    //d = min(d, spaceframe(p-vec3(0,0,0.5),1.0) - 0.01);

    if(variant())
    {
        // small
        d = min(d, spaceframes(p-vec3(0,0,0.5), 10, 1) - 0.03);
        //d = min(d, sdSphere(p-vec3(0,0,2), 1.5)); // tank
        d = min(d, sdCappedCylinder(p-vec3(0,0,1), 1.5, 1.0)); // tank
    }
    else
    {
        d = min(d, spaceframes(p-vec3(0,0,0.5), 45, 8) - 0.03);
        //d = min(d, sdSphere(p-vec3(0,0,8.45), 8.45)); // tank
        //d = min(d, sdCappedCylinder(p-vec3(0,0,1), 8.45, 8.0)); // tank
    }
    return d;
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal( vec3 p ) 
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ) + 
                      k.yyx*map( p + k.yyx*h ) + 
                      k.yxy*map( p + k.yxy*h ) + 
                      k.xxx*map( p + k.xxx*h ) );
}

float trace(vec3 ro, vec3 rd, float t0, float t1)
{
    for(float t = t0; t < t1;)
    {
        float h = map(ro + rd * t);
        if(h < EPSILON)
            return t;
        t += h;
    }
    return t1;
}
/*
float trace(vec3 ro, vec3 rd, float t0, float t1)
{
    if(t0 < 1.0)
    {
        for(float t = t0; t < 1.0;)
        {
            float h = map(ro + rd * t);
            if(h < EPSILON)
                return t;
            t += h;
        }
        t0 = 1.0;
    }

    for(float t = t0; t < t1;)
    {
        float h = map(ro + rd * t);
        if(h < EPSILON * t) // optimization: coarser epsilon in the distance
            return t;
        t += h;
    }

    return t1;
}
*/
// https://iquilezles.org/articles/rmshadows
float shadow( in vec3 ro, in vec3 rd, float mint, float maxt )
{
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<EPSILON )
            return 0.0;
        t += h;
    }
    return 1.0;
}

// https://iquilezles.org/articles/rmshadows
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    float ph = 1e20;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor );
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 2.0 * (fragCoord.xy) / iResolution.xy - 1.0;
    p.x *= float(iResolution.x) / float(iResolution.y);
    float mx = 2.0 * pi * float(iMouse.x) / float(iResolution.x);
    float my = pi / 2.0 + pi / 2.0 * float(iMouse.y) / float(iResolution.y);
    mx = (iMouse.x > 10.0) ? mx : 2.0 * pi * fract(iTime * 0.01);
    my = (iMouse.y > 10.0) ? my : variant() ? 1.25 * pi / 2.0 : pi / 2.0;

    mat2 Yaw = mat2(vec2(cos(mx), sin(mx)), vec2(-sin(mx), cos(mx)));
    vec3 ro = variant() ? vec3(0.0, 1.0, -8.0 ) : vec3(0.0, 5.0, -20.0 );
    //mat2 Pitch = mat2(vec2(0.0, 1.0), vec2(-1.0, 0.0));
    mat2 Pitch = mat2(vec2(cos(my), sin(my)), vec2(-sin(my), cos(my)));
    ro.yz = Pitch * ro.yz;
    ro.xy = Yaw * ro.xy;

    vec3 rd = normalize(vec3(0.5 * p.xy, 1.0)); // NOTE: omitting normalization results in clipped edges artifact
    rd.yz = Pitch * rd.yz;
    rd.xy = Yaw * rd.xy;

    float dist = trace(ro, rd, 0.0, DIST_MAX);
    vec3 dst = ro + rd * dist;
    vec3 n = normal(dst);

    vec3 lightdir = normalize(vec3(1.0, 1.0, 1.0));
    vec3 ambient = vec3(0.1);
    float brightness = max(dot(lightdir, n), 0.0);
    //brightness *= shadow(ro+rd*dist,lightdir, 0.01, DIST_MAX );
    brightness *= softshadow(ro+rd*dist,lightdir, 0.01, DIST_MAX, 100. );
    //vec3 color = (n * 0.5 + 0.5);
    vec3 color = mix(vec3(0.25), vec3(1), step(0.01, dst.z)); // quick hack to color ground differently
    color = (ambient * calcAO(dst, n) + brightness) * color;
    //color = (ambient + calcAO(dst, n) * brightness) * color;

    //color = mix(color, vec3(0.0), step(DIST_MAX, dist));
    color = mix(color, vec3(0.0), smoothstep(DIST_MAX-1.0, DIST_MAX, dist));

    fragColor = vec4(sqrt(color), 1.0); // approximate gamma
}
