void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float x = uv.s - 0.5;
    float y = uv.t - 0.5;
    x *= iResolution.x / iResolution.y;
    float time = iTime;
    vec3 color = vec3(0.0);

    float r = sqrt(x*x + y*y);
    float t = atan(y, x);
    float cls = t;
    t += time * 0.5;
    t += 0.1 * sin(pow(r * 2.2, 2.0) * 15.0 + time * -4.0);
    color.r = sin(cls + t - 0.224);
    color.g = sin(cls + t + 0.7);
    color.b = sin(cls + t + 1.2);
    color *= 0.764 + 0.620 * sin(t * 20.0);

    fragColor = vec4(color, 1.0);
}
