// Circle
float circleRadius = 0.35;

// Noise
float noiseScale = 0.1;
vec2 noiseScroll = vec2(1.0, 1.0) * 0.075;
float noiseIntensity = 0.07;

// Ripple
float rippleSpeed = 0.5;
float ripples = 7.0;
float rippleSize = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Circular pixel coordinates
    vec2 uvCircle = uv.xy * vec2(ratio, 1.0);

    // Noise
    vec4 noise = texture(iChannel0, uvCircle * noiseScale + iTime * noiseScroll);
    noise = (noise - 0.5) * 2.0;
    noise *= noiseIntensity;



    // Distance from the middle of the screen
    float dist =  distance(vec2(ratio * 0.5, 0.5 - noiseIntensity),uvCircle.xy + noise.xy);

    // Circle
    float circle = smoothstep(circleRadius + 0.005, circleRadius, dist);

    // Ripple
    float ripple = 
        smoothstep(circleRadius + 0.01,
            circleRadius - 0.01,
        mod(dist * ripples - iTime * rippleSpeed, circleRadius + rippleSize)) * circle;



    // Output to screen
    fragColor = vec4(ripple, ripple, ripple, 1.0);
    //fragColor = vec4(dist, dist, dist, 1.0);
}
