void mainImage(out vec4 RGBA, in vec2 XY)
{
	vec4 mc = M; mc.xy = MC; // mouse coords
	float z = Z; // mouse y zoom
    float e = E; // spiral exponent
    float s = SCALE;
    if (mc.z > 0.0) {s = 2.5; e = -mc.y;}
	vec2 sc = SC; // screen coords
	float px = PX; // pixel radius

    vec3 purp = vec3(0.4, 0.3, 0.9);
	vec3 c = purp*vec3(pow(min(length(sc), 1.0/length(sc)), 2.0))*0.5; // background
	float lim = 25.0;
	for(float i = 0.0; i < 1.0; i += 1.0/lim)
	{
		vec3 h = vec3(i, 1.0-abs(i+i-1.0), 1.0-i); // Xor's chromatic
		c += texture(iChannel0, mix(XY/R, vec2(0.5), -sign(e)*0.25*i), AA).rgb*h*0.25;
	}
	c += FS(px*8.0)*purp;
    c += FS(px);
	RGBA = vec4(c, 1.0);
}

#define R iResolution.xy
#define T iTime
#define M iMouse
#define SCALE 1000.0
#define E (1.0)
#define N ((T/60.0)+50.0)
#define PI radians(180.0)
#define TAU (PI*2.0)
#define CS(a) vec2(cos(a), sin(a))
#define FS(w) fs(sc, e, N, w, sqrt(N)*200.0)
#define SC ((XY-0.5*R)/R.y*s*z)
#define MC ((M.xy-0.5*R.xy)/R.y*2.0)
#define Z (mc.z > 0.0) ? pow(1.0-abs(mc.y), 3.0*sign(mc.y)): 1.0
#define PX length(fwidth(sc/1.414))
#define AA 0.5

// fraction spiral: decimal(n) to visual fraction
// screen coords, exponent, value, width, rotation
float fs(vec2 sc, float e, float n, float w, float ro)
{
	float t = TAU*sqrt(abs(n))*sign(n);
	float r = round(exp(log(length(sc))/e));
	vec2 xy = sc-CS(ro-t*sqrt(r*2.0))*pow(r, e);
	return max(0.0, 1.0-length(xy/w));
}

void mainImage(out vec4 RGBA, in vec2 XY)
{
	vec4 mc = M; mc.xy = MC; // mouse coords
	float z = Z; // mouse y zoom
    float e = E; // spiral exponent
    float s = SCALE;
    if (mc.z > 0.0) {s = 2.5; e = -mc.y;}
	vec2 sc = SC; // screen coords
	float px = PX; // pixel radius

    vec3 c = vec3(texture(iChannel0, mix(XY/R, vec2(0.5), 0.002), AA).rgb)*0.95; // subtle after-image
    c += FS(px*2.0); // spiral
	RGBA = vec4(c, 1.0);
}
