void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_pix_coo_nor = (fragCoord.xy - iResolution.xy*0.5)/iResolution.y;
    vec2 o_mou_coo_nor = (iMouse.xy - iResolution.xy*0.5)/iResolution.y;

    if(iMouse.w == 0.){
        o_mou_coo_nor = vec2(0.2, 0.0);
    }
    float n_freqs = o_mou_coo_nor.x*1000.0;

    vec2 o_opcn_big = o_pix_coo_nor*vec2(n_freqs);
    vec2 o_opcn_big_mod = fract(o_opcn_big);

    fragColor = vec4(length(o_opcn_big_mod-vec2(0.5)));

}
