float wow(vec2 uv, float battery)
{
 	float val = smoothstep(0.3, 0.2, length(uv));
 	float bloom = smoothstep(0.6, -0.4, length(uv));
    float cut_val = sin((uv.y + iTime * 0.2) * 70.0) + (uv.y + 0.15)* 8.;
    cut_val = clamp(cut_val, 0., 1.);
    return cut_val * val  + bloom * 0.6;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 col = vec3(0.);
    col = vec3(1., 0.2, 1.);
    float value = wow(uv, 1.);
    col = mix(vec3(0.), col, value);
    fragColor = vec4(col,1.0);
}
