void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // RedColor
    vec3 col = vec3(1.,0.,0.);

    float V = abs(sin(iTime));

    vec3 adjustedCol=mix(col.rgb,vec3(0.),V);

    // Output to screen
    fragColor = vec4(adjustedCol,1.0);
}
