float threshold = .5;
int iters = 4;

#define noise2D(p) float(((p).x * 68657387u ^ (p).y * 361524851u + (p).x) % 890129u) / 890128.

bool labyrinth(uvec2 U,float n2){
    bvec2 b = bvec2(U & 1u);
    return (b.x && b.y || b.x != b.y  && n2 < threshold);
}

