void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    float rot = 0.0;
    if(uv.x >= 0.0){
        rot = (-atan(uv.y/abs(uv.x))+1.5708)/2.0;
    } else {
        rot = (atan(uv.y/abs(uv.x))+4.7124)/2.0;
    }

    float d = length(uv);
    d -= 0.5;
    d = abs(d);
    d = 0.05/d;

    vec3 col = vec3(rot/6.2831,0.0,0.0);

    col*= d;

    // Output to screen
    fragColor = vec4(col,1.0);
}
