// Forked from https://www.shadertoy.com/view/DsscWn
// Palette from https://www.shadertoy.com/view/dlVSDK

vec3 palette(float t) {

    vec3 a = vec3( .2,  .7,     .4    );
    vec3 b = vec3( .5,  .8,     .5    );
    vec3 c = vec3(1. , 2.,     1.     );
    vec3 d = vec3( .0,  .33333, .66666);

    return a + b*cos(6.28318*(c*t + d));
}

void mainImage( out vec4 c, vec2 p ) {
    vec2 v = iResolution.xy;
    float zoom = -5. + abs(sin(iTime * 0.05)) * 4.;  // oscillates zoom
    p = ((p-v*.5)*.4 / v.y) / zoom; // Apply zoom
    p += p * sin(dot(p, p)*20.-iTime) * .04;
    c *= 0.;
    for (float i = .5 ; i < 8. ; i++)
        p = abs(2.*fract(p-.5)-1.) * mat2(cos(.01*(iTime+iMouse.x*.1)*i*i + .78*vec4(1,7,3,1))),
        c += exp(-abs(p.y)*5.) * (cos(vec4(.0,.7,1.5,0)*i)*.5+.2);  // Reduced values for darker colors
    //c -= vec4(0.3, 0.3, 0.3, 0);  // Subtct base color for contrast
    c = vec4(palette(c.x), 1.0);
    c = clamp(c, 0.0, 1.0); // Ensure colors stay within valid range
}
