// Rough explanation:
// O accumulates value as ray moves
// abs(tex-.3) lets us move inside the noise, is step distance
// vec3(I/res.y,1) is ray direction
// so O += accumulate(p += step distance * ray direction)

void mainImage( out vec4 O, vec2 I )
{
    vec3 p;
    for(O-=O; O.w++ < 2e2; p+=abs(texture(iChannel0, .02*(p+iTime)).r-.2)
                            *vec3(I/7e2, 2))     
        O += p.z/1e4;
}
