vec2 hash(vec2 p){
    p*=mat2(86.,-78.,-781.,538.);
    return fract(sin(p)*176575.);
}

float norm(vec2 p,float n){
    p=pow(p,vec2(n));
    return pow(p.x*p.x+p.y*p.y,1./n);
}

vec3 voronoi(vec2 p){
    vec2 n=floor(p);
    vec2 f=p-n;
    vec2 res=vec2(10.);
    vec2 point;
    for(float i=-1.;i<=1.;i++){
        for(float j=-1.;j<=1.;j++){
            vec2 b=vec2(i,j);
            point=hash(n+b);
            vec2 l=point+b-f;
            float g=norm(l,200.);
            if(g<res.x){
                res.y=res.x;
                res.x=g;
            }
            else if(g<res.y){
                res.y=g;
            }   
        }
    }
    return vec3(res.x,point);
}

vec2 voronoidifferential(vec2 p,float n){
    float eps=0.01;
    return vec2(voronoi((p+vec2(eps,0.))*n).x-voronoi(p*n).x,
                 voronoi((p+vec2(0.,eps))*n).x-voronoi(p*n).x);
}

float sphere(vec3 p){
    return length(p)-2.;
}
vec3 bg(vec3 rd){
    return mix(vec3(0.2,0.3,0.8),vec3(0.7,0.1,0.2),(rd.x*rd.x*(-rd.x)*(-rd.y)+1.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x*=iResolution.x/iResolution.y;
    vec3 col;
    //differential
    vec3 st=voronoi(uv*6.);
    float eps=0.01;
    vec2 ff=voronoidifferential(uv,6.);
    float d=smoothstep(0.85,-0.1,length(ff))*step(0.1,st.x);

    //cam
    vec3 rd=normalize(vec3(uv.x,uv.y,1.));
    vec3 ro=vec3(0.,0.,-3.);
    vec3 p=ro;
    float l=0.;
    for(int i=0;i<30;i++){
        p=ro+rd*l;
        float d=sphere(p);
        if(d<eps)break;
        l+=d;
    }
    if(l<10.){
        vec2 ffxy=voronoidifferential(p.xy,6.);
        //vec3 stxy=voronoi(p.xy*6.);
        float o=smoothstep(0.06+pow(sin(iTime+hash(vec2(iTime,-iTime)).x),-6.)*0.06,0.,length(ffxy));
        col=vec3(o*o*o,o,o*o)*0.75;//*vec3(stxy.xzy);
    }
    else col=vec3(1.-d,st.zy)*bg(rd);
    col=pow(col,vec3(1./2.2));

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 noise(float time)
{
    float t = 5.-sin(time)*8.;
    float f = sin(6.2831*10.5*t)+50.;
    f += sin(80.0*6.2831*t);
    f = sin(6.2831*f*t);
    return vec2(f);
}   


vec2 mainSound( int samp, float time )
{
    vec2 s= noise(time)*0.1;
    return s;
}
