void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec3 drkblue = vec3(0.02, 0.25, 0.91);
    vec3 ltblue = vec3(0.1, 0.91, 0.98);

    float intensity = fract(uv.y*5.0 + sin(uv.x*10.0+sin(iTime)));
    vec3 col = intensity*drkblue + (vec3(1,1,1)-intensity)*ltblue;

    fragColor = vec4(col,1.0);
}
