void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor  = vec4(0.);

    float n_tau = 6.2831;
    vec2 o_trn_fc_nor = (fragCoord.xy -iResolution.xy*.5)/ iResolution.y;


    o_trn_fc_nor *= 3.3;//zoom out

    float n_d = length(o_trn_fc_nor);
    // n_d *= n_d*n_d*n_d;
    float n_d_circ = abs(n_d-.5);
    n_d_circ = pow(n_d_circ, 1./2.);

    float n_its = 6.;
    // float n_ang = atan(o_trn_fc_nor.y, o_trn_fc_nor.x);
    // float n_ang_nor = (n_ang/n_tau)+.5;
    // float n_ang_nor_snapped = 1./ floor(n_ang_nor / (1./6.));
    float n_d_min = 1.;
    for(float n_it = 0.; n_it < n_its; n_it+=1.){

        float n_ang_nor_snapped = (1./n_its)*n_it;
        float n_radius = .5;
        vec2 o_p = vec2(
            sin(n_ang_nor_snapped*n_tau)*n_radius,
            cos(n_ang_nor_snapped*n_tau)*n_radius
        );
        float n_radius2 = (sin(iTime+(n_ang_nor_snapped*n_tau)*2.)*.5+.5)*2.+1.;
        float n_d2 = length((o_trn_fc_nor-o_p)*n_radius2);

        // n_d *= n_d*n_d*n_d;
        float n_d2_circ = abs(n_d2-.5);
        // n_d2_circ = pow(n_d2_circ, 1./((sin(iTime+n_it)*.5+.5)+1.));
        n_d2_circ = pow(n_d2_circ, 1./2.);

        n_d_min = min(n_d_min, n_d2_circ);
        // fragColor +=n_d2_circ;

    }
    n_d_min = min(n_d_min, n_d_circ);

    fragColor +=n_d_min;
    fragColor = 1.-fragColor;

    // fragColor = vec4(n_ang_nor_snapped);
}
