float sqWave(float x, float freq, float amp) {
    return abs(mod(floor(x * freq), 2.f * amp));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )


{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float xy = fragCoord.x + fragCoord.y;
    float freq = 0.03; 
    float rSine = smoothstep(0., 1., sin(xy * freq));//sqWave(xy, freq, 1.f); 
    float gSine = smoothstep(0., 1., sin(xy * freq + iTime));// sqWave(xy + iTime * 100., freq, 1.f); 
    float bSine = smoothstep(0., 1., sin(xy * freq - iTime));
    // Time varying pixel color
    float r = rSine;
    float g = gSine;
    float b = bSine;
    vec4 col = vec4(r, g, b, 1.0);

    // Output to screen
    fragColor = col;
}
