void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;    

	fragColor = texture(iChannel0, uv);
}

mat3 mats[9];

float cutit(float inp){
return clamp(inp, 0., 1.);
}

float checkX(float x){
if(x<1.){
return iResolution.x-1.;
}
if(x>iResolution.x-1.){
return 1.;
}
return x;
}

float checkY(float y){
if(y<1.){
return iResolution.y-1.;
}
if(y>iResolution.y-1.){
return 1.;
}
return y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

float a = 0.5;
float b = -.5;
float c = 0.065;
float d = 0.0015;

//The configuration tensor (change these values for different patterns):
mats[0] = mat3(a, d, 0, 0, a, d, d, 0, a);
mats[1] = mat3(b, 0, -d, -d, b, 0, 0, -d, b);
mats[2] = mat3(a, d, 0, 0, a, d, d, 0, a);
mats[3] = mat3(b, 0, -d, -d, b, 0, 0, -d, b);
mats[4] = mat3(c, 0, 0, 0, c, 0, 0, 0, c);
mats[5] = mat3(b, 0, -d, -d, b, 0, 0, -d, b);
mats[6] = mat3(a, d, 0, 0, a, d, d, 0, a);
mats[7] = mat3(b, 0, -d, -d, b, 0, 0, -d, b);
mats[8] = mat3(a, d, 0, 0, a, d, d, 0, a);

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 rtot = vec3(0.,0.,0.);
    int ind = 0;    

//the convolution kernel is just the Moore neighborhood of a given cell
//take the RGB values as vectors and apply a matrix transform specified via the configuration tensor, then sum
    for(float i = -1.0; i <= 1.0; i += 1.0)
    {
        for( float j = -1.0; j <= 1.0; j += 1.0)
        {
        	vec4 data = texelFetch(iChannel0, ivec2(checkX(fragCoord.x+i),checkY(fragCoord.y+j)), 0);
        	rtot += mats[ind]*data.rgb;
            ind++;
        }
    }


    vec3 rfin = vec3(cutit(abs(rtot.x)), cutit(abs(rtot.y)), cutit(abs(rtot.z)));    

//initialize random colors for each cell    
    if(iFrame < 10) {
    rfin = vec3(fract(sin(iDate[3]/100.*dot(uv, vec2(12.9898, 78.233))) * 43758.5453), fract(sin(iDate[3]/100.*dot(uv, vec2(12.9898, 78.233))) * 53768.5453), fract(sin(iDate[3]/100.*dot(uv, vec2(12.9898, 78.233))) * 41258.5453));
    }

//paint on pixels manually with the mouse
    if(iMouse.z > 0.0 && abs(fragCoord.x-iMouse.x)<1.0 && abs(fragCoord.y-iMouse.y)<1.0){
    rfin = vec3(1.0,1.0,1.0);
    }


    fragColor = vec4(rfin,1.0);
}

//anti-flicker buffer
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;    
    vec4 col1 = texture(iChannel0, uv);
    vec4 col2 = texture(iChannel1, uv);
    fragColor = (col1+col2)*.55;
}
