#define ROT(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float tunnel(vec2 uv) {
float r = pow(pow(abs(uv.x),18.0)+pow(abs(uv.y),10.0), 1.8/28.0);
return r;
}

vec2 texture(vec2 uv) {
uv = fract(2.0*uv) - 0.5;
return uv;

}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

vec2 uv = (fragCoord.xy/iResolution.xy) * 2.0 - 1.0;

// tunnel effect
float r = tunnel(uv);

// rotating
uv *= ROT(iTime);

// texturing
vec2 tex = texture(uv);

// lighting
vec3 col = vec3(uv, 0.0) + 0.12;

// apply tunnel

col *= 1.0/r;

// final color
fragColor = vec4(col, 1.0);

}
